  created: function created() {
    //获取用户列表
    this.getUserList(); //预备库下拉框筛选项

    this.selectionMap(); //获取供应商列表

    this.supplierManagerList();
  },
  computed: {
    //图片前缀
    domain: function domain() {
      return this.$store.state.domain;
    }
  },
  mounted: function mounted() {
    //获取表格最大高度
    this.onResize();
    window.addEventListener("resize", this.onResize());
  },
  methods: {
    //监听屏幕大小变化
    onResize: function onResize() {
      var _this = this;

      this.$nextTick(function () {
        var card_box_height = document.getElementById("card_box").offsetHeight;
        var table_title_height = document.getElementById("table_title").offsetHeight;
        var bottom_row_height = document.getElementById("bottom_row").offsetHeight;
        _this.max_height = card_box_height - table_title_height - bottom_row_height - 60 + "px";
      });
    },
    //获取用户列表
    getUserList: function getUserList() {
      var _this2 = this;

      resource.supplierAjaxUser().then(function (res) {
        if (res.data.code == 1) {
          _this2.user_list = res.data.data;
        } else {
          _this2.$message.warning(res.data.msg);
        }
      });
    },
    //预备库下拉框筛选项
    selectionMap: function selectionMap() {
      var _this3 = this;

      resource.selectionMap().then(function (res) {
        if (res.data.code == 1) {
          var data = res.data.data;
          _this3.classification_list = data.classification; //分类

          _this3.area_list = data.area; //区域

          _this3.settlement_method_list = data.settlement_method; //结算方式

          _this3.cost_performance_list = data.cost_performance; //性价比

          _this3.cooperativeness_list = data.cooperativeness; //合作程度
        } else {
          _this3.$message.warning(res.data.msg);
        }
      });
    },
    //点击查询
    searchData: function searchData() {
      if (this.start_price != '' && !this.isPrice.test(parseFloat(this.start_price))) {
        this.$message.warning('价格必须大于0且最多两位小数');
      } else if (this.end_price != '' && !this.isPrice.test(parseFloat(this.end_price))) {
        this.$message.warning('价格必须大于0且最多两位小数');
      } else if (parseFloat(this.start_price) > parseFloat(this.end_price)) {
        this.$message.warning('起始价格不能高于结束金额');
      } else {
        this.checkPage(1);
      }
    },
    //获取供应商列表
    supplierManagerList: function supplierManagerList() {
      var _this4 = this;

      var arg = {
        supplier_name: this.supplier_name,
        //供应商名称
        developer: this.developer,
        //开发人员姓名
        start_time: this.develop_date && this.develop_date.length > 0 ? this.develop_date[0] : "",
        end_time: this.develop_date && this.develop_date.length > 0 ? this.develop_date[1] : "",
        classification: this.classification,
        //分类
        area: this.area,
        //区域
        main_business: this.main_business,
        //主营
        scpl: this.scpl,
        //擅长品类
        supply_free_factory: this.supply_free_factory,
        //是否自有工厂
        supply_design: this.supply_design,
        //是否自有设计能力
        settlement_method: this.settlement_method,
        //结算方式
        supply_return_exchange: this.supply_return_exchange,
        //是否可退换货
        supply_replace_send: this.supply_replace_send,
        //是否可代发
        cost_performance: this.cost_performance,
        //性价比
        filling_status: this.filling_status,
        //填报状态
        cooperativeness: this.cooperativeness,
        //合作程度
        qualified_status: this.qualified_status,
        //合格状态
        pagesize: 20,
        page: this.page
      };

      if (this.start_price != '' || this.end_price != '') {
        arg['price_range'] = "".concat(this.start_price, "_").concat(this.end_price);
      }

      this.loading = true;
      resource.reserveIndex(arg).then(function (res) {
        if (res.data.code == 1) {
          _this4.loading = false;
          _this4.data = res.data.data;
          _this4.button_list = res.data.data.button_list;
        } else {
          _this4.$message.warning(res.data.msg);
        }
      });
    },
    //切换开发人员
    changeUser: function changeUser(e) {
      var maintainer_arr = this.user_list.filter(function (item) {
        return item.user_id == e;
      });
      this.info_arg.developer = maintainer_arr.length > 0 ? maintainer_arr[0].real_name : "";
    },
    //下载模版
    downTemplate: function downTemplate() {
      window.open("".concat(this.downLoadUrl, "/template/\u4F9B\u5E94\u5546\u4E0A\u4F20\u6A21\u677F.xlsx"));
    },
    //导入
    uploadCsv: function uploadCsv() {
      var _this5 = this;

      if (this.$refs.csvUpload.files.length > 0) {
        var files = this.$refs.csvUpload.files;
        resource.batchAdd({
          file: files[0]
        }).then(function (res) {
          _this5.$refs.csvUpload.value = null;
          _this5.import_dialog = false;

          if (res.data.code == 1) {
            _this5.$message.success(res.data.msg);

            _this5.page = 1; //获取列表

            _this5.supplierManagerList();
          } else {
            _this5.$message.warning(res.data.msg);
          }
        });
      }
    },
    //切换页码
    checkPage: function checkPage(v) {
      this.page = v; //获取供应商列表

      this.supplierManagerList();
    },
    //填报点击添加或编辑
    addFn: function addFn(type, reserve_id) {
      var _this6 = this;

      this.add_type = type;

      if (type == '1') {
        this.add_title = '添加';
        this.edit_dialog = true;
      } else if (type == '2') {
        this.add_title = '编辑';
        this.reserve_id = reserve_id;
        resource.reserveEditGet({
          reserve_id: this.reserve_id
        }).then(function (res) {
          if (res.data.code == 1) {
            var data = res.data.data;

            for (var k in _this6.info_arg) {
              _this6.info_arg[k] = data[k];
            }
          } else {
            _this6.$message.warning(res.data.msg);
          }
        });
        this.edit_dialog = true;
      }
    },
    //填报弹窗关闭
    closeEdit: function closeEdit() {
      for (var k in this.info_arg) {
        this.info_arg[k] = "";
      }

      this.check_status = 1; //审核状态

      this.remark = ""; //拒绝原因

      this.company_name = ""; //公司名称

      this.business_license_img = []; //工商营业执照

      this.company_img = []; //公司照片
    },
    //点击填报编辑或添加的提交
    submitAddEdit: function submitAddEdit() {
      var _this7 = this;

      if (this.info_arg.start_price != '' && !this.isPrice.test(parseFloat(this.info_arg.start_price))) {
        this.$message.warning('价格必须大于0且最多两位小数');
        return;
      } else if (this.info_arg.end_price != '' && !this.isPrice.test(parseFloat(this.info_arg.end_price))) {
        this.$message.warning('价格必须大于0且最多两位小数');
        return;
      } else if (parseFloat(this.info_arg.start_price) > parseFloat(this.info_arg.end_price)) {
        this.$message.warning('起始价格不能高于结束金额');
        return;
      }

      if (this.add_type == '1') {
        var arg = JSON.parse(JSON.stringify(this.info_arg));
        arg['price_range'] = "".concat(arg.start_price, "_").concat(arg.end_price);
        delete arg.start_price;
        delete arg.end_price;
        resource.reserveAdd(this.info_arg).then(function (res) {
          if (res.data.code == 1) {
            _this7.$message.success(res.data.msg);

            _this7.edit_dialog = false; //获取供应商列表

            _this7.supplierManagerList();
          } else {
            _this7.$message.warning(res.data.msg);
          }
        });
      } else if (this.add_type == '2') {
        var _arg = JSON.parse(JSON.stringify(this.info_arg));

        _arg['price_range'] = "".concat(_arg.start_price, "_").concat(_arg.end_price);
        delete _arg.start_price;
        delete _arg.end_price;
        _arg['reserve_id'] = this.reserve_id;
        resource.reserveEditPost(_arg).then(function (res) {
          if (res.data.code == 1) {
            _this7.$message.success(res.data.msg);

            _this7.edit_dialog = false; //获取供应商列表

            _this7.supplierManagerList();
          } else {
            _this7.$message.warning(res.data.msg);
          }
        });
      }
    },
    //点击填报阶段审核/查看
    checkInfo: function checkInfo(type, reserve_id) {
      var _this8 = this;

      this.add_type = type;
      this.add_title = this.add_type == '3' ? '详情' : '审核';
      this.reserve_id = reserve_id;
      resource.reserveInfo({
        reserve_id: this.reserve_id
      }).then(function (res) {
        if (res.data.code == 1) {
          var data = res.data.data;

          for (var k in _this8.info_arg) {
            _this8.info_arg[k] = data[k];
          }

          var price_range = data.price_range.split('~');
          _this8.info_arg.start_price = price_range[0];
          _this8.info_arg.end_price = price_range[1];
        } else {
          _this8.$message.warning(res.data.msg);
        }
      });
      this.edit_dialog = true;
    },
    //提交填报审核
    submitCheck: function submitCheck() {
      var _this9 = this;

      if (this.check_status == 0 && this.remark == '') {
        this.$message.warning('请输入拒绝原因');
        return;
      }

      var arg = {
        reserve_id: this.reserve_id,
        status: this.check_status,
        remark: this.remark
      };
      resource.checkInfo(arg).then(function (res) {
        if (res.data.code == 1) {
          _this9.$message.success(res.data.msg);

          _this9.edit_dialog = false; //获取供应商列表

          _this9.supplierManagerList();
        } else {
          _this9.$message.warning(res.data.msg);
        }
      });
    },
    //点击转合格编辑或添加
    zhuanFn: function zhuanFn(type, reserve_id) {
      var _this10 = this;

      this.reserve_id = reserve_id;
      this.zhuan_type = type;

      if (type == '1') {
        //填写
        this.zhuan_dialog = true;
      } else {
        //编辑
        var arg = {
          reserve_id: this.reserve_id
        };
        resource.reserveQualifiedEditGet(arg).then(function (res) {
          if (res.data.code == 1) {
            var data = res.data.data;
            _this10.company_name = data.company_name; //公司名称
            //工商营业执照

            _this10.business_license_img = [];

            if (data.business_license) {
              var business_license_img = data.business_license.split(',');
              business_license_img.map(function (item) {
                var business_img = {
                  urls: item,
                  show_icon: false
                };

                _this10.business_license_img.push(business_img);
              });
            } //公司照片


            _this10.company_img = [];

            if (data.company_img) {
              var company_img = data.company_img.split(',');
              company_img.map(function (item) {
                var company_img = {
                  urls: item,
                  show_icon: false
                };

                _this10.company_img.push(company_img);
              });
            }

            _this10.zhuan_dialog = true;
          } else {
            _this10.$message.warning(res.data.msg);
          }
        });
      }
    },
    //监听工商营业执照图片列表回调
    businessCallbackFn: function businessCallbackFn(img_arr) {
      this.business_license_img = img_arr;
    },
    //监听公司图片列表回调
    companyCallbackFn: function companyCallbackFn(img_arr) {
      this.company_img = img_arr;
    },
    //提交/编辑转合格
    commitZhuan: function commitZhuan() {
      var _this11 = this;

      if (this.company_name == '') {
        this.$message.warning('请输入公司名称');
        return;
      } else if (this.business_license_img.length == 0) {
        this.$message.warning('请上传营业执照');
        return;
      } else if (this.company_img.length == 0) {
        this.$message.warning('请上传公司图片');
        return;
      }

      if (this.zhuan_type == '1') {
        //添加
        var arg = {
          reserve_id: this.reserve_id,
          company_name: this.company_name,
          company_img: this.company_img.join(','),
          business_license: this.business_license_img.join(',')
        };
        resource.qualified(arg).then(function (res) {
          if (res.data.code == 1) {
            _this11.$message.success(res.data.msg);

            _this11.zhuan_dialog = false; //获取供应商列表

            _this11.supplierManagerList();
          } else {
            _this11.$message.warning(res.data.msg);
          }
        });
      } else {
        //编辑
        var _arg2 = {
          reserve_id: this.reserve_id,
          company_name: this.company_name
        };
        var company_img = [];
        this.company_img.map(function (item) {
          company_img.push(item.urls ? item.urls : item);
        });
        _arg2['company_img'] = company_img.join(',');
        var business_license = [];
        this.business_license_img.map(function (item) {
          business_license.push(item.urls ? item.urls : item);
        });
        _arg2['business_license'] = business_license.join(',');
        resource.reserveQualifiedEditPost(_arg2).then(function (res) {
          if (res.data.code == 1) {
            _this11.$message.success(res.data.msg);

            _this11.zhuan_dialog = false; //获取供应商列表

            _this11.supplierManagerList();
          } else {
            _this11.$message.warning(res.data.msg);
          }
        });
      }
    },
    //关闭转合格弹窗
    closeZhuan: function closeZhuan() {
      this.company_name = "";
      this.company_img = [];
      this.business_license_img = [];
    },
    //点击删除
    deleteFn: function deleteFn(reserve_id) {
      var _this12 = this;

      this.$confirm('确认删除该供应商?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        var arg = {
          reserve_id: reserve_id
        };
        resource.reserveDel(arg).then(function (res) {
          if (res.data.code == 1) {
            _this12.$message.success(res.data.msg); //获取供应商列表


            _this12.supplierManagerList();
          } else {
            _this12.$message.warning(res.data.msg);
          }
        });
      }).catch(function () {
        _this12.$message({
          type: 'info',
          message: '已取消'
        });
      });
    },
    //点击合格待审核
    hgAuditFn: function hgAuditFn(reserve_id) {
      var _this13 = this;

      this.add_type = '5';
      this.add_title = '转合格审核';
      this.reserve_id = reserve_id;
      resource.reserveInfo({
        reserve_id: this.reserve_id
      }).then(function (res) {
        if (res.data.code == 1) {
          var data = res.data.data;

          for (var k in _this13.info_arg) {
            _this13.info_arg[k] = data[k];
          }

          var price_range = data.price_range.split('~');
          _this13.info_arg.start_price = price_range[0];
          _this13.info_arg.end_price = price_range[1];
          _this13.company_name = data.company_name; //公司名称
          //工商营业执照

          _this13.business_license_img = [];
          var business_license_img = data.business_license.split(',');
          business_license_img.map(function (item) {
            _this13.business_license_img.push(_this13.domain + item);
          }); //公司照片

          _this13.company_img = [];
          var company_img = data.company_img.split(',');
          company_img.map(function (item) {
            _this13.company_img.push(_this13.domain + item);
          });
        } else {
          _this13.$message.warning(res.data.msg);
        }
      });
      this.edit_dialog = true;
    },
    //转合格审核提交
    zhgAudit: function zhgAudit() {
      var _this14 = this;

      if (this.audit_status == 0 && this.audit_remark == '') {
        this.$message.warning('请输入拒绝原因');
        return;
      }

      var arg = {
        reserve_id: this.reserve_id,
        status: this.audit_status,
        remark: this.audit_remark
      };
      resource.checkQualified(arg).then(function (res) {
        if (res.data.code == 1) {
          _this14.$message.success(res.data.msg);

          _this14.edit_dialog = false; //获取供应商列表

          _this14.supplierManagerList();
        } else {
          _this14.$message.warning(res.data.msg);
        }
      });
    }
  },
  filters: {
    status: function status(v) {
      switch (v) {
        case 0:
          return '填报后待审核';

        case 1:
          return '填报后审核通过(待转合格) ';

        case 2:
          return '填报后审核拒绝';

        case 3:
          return '转合格待审核';

        case 4:
          return '转合格同意';

        case 5:
          return '转合格拒绝';
      }
    }
  },
  components: {
    TableTitle: TableTitle,
    PaginationWidget: PaginationWidget,
    UploadFile: UploadFile
  }
};
