import "core-js/modules/es6.regexp.split";
import resource from "../../../../../api/chain_resource.js";
import TableTitle from "../../../components/table_title.vue";
import PaginationWidget from "../../../../../components/pagination_widget.vue";
import UploadFile from "../../../../../components/upload_file.vue";
export default {
  data: function data() {
    return {
      loading: false,
      max_height: 0,
      page: 1,
      //页码
      data: [],
      //获取的数据
      button_list: {},
      total: 0,
      i_id: "",
      show_dialog: false,
      //弹窗
      dialog_title: "",
      //弹窗标题
      shooting_style_name: "",
      //拍摄风格
      img: [],
      //已上传的图片列表（参数）
      img_list: [] //已上传的图片列表（显示）

    };
  },
  props: {
    //商品ID
    style_id: {
      type: String,
      default: ""
    },
    //商品编号
    style_name: {
      type: String,
      default: ""
    }
  },
  created: function created() {
    //获取风格图列表
    this.styleImageList();
  },
  computed: {
    //图片前缀
    domain: function domain() {
      return this.$store.state.domain;
    }
  },
  destroyed: function destroyed() {
    window.removeEventListener("resize", function () {});
  },
  mounted: function mounted() {
    //获取表格最大高度
    this.onResize();
    window.addEventListener("resize", this.onResize());
  },
  methods: {
    //监听屏幕大小变化
    onResize: function onResize() {
      var _this = this;

      this.$nextTick(function () {
        var card_box_height = document.getElementById("card_box").offsetHeight;
        var table_title_height = document.getElementById("table_title").offsetHeight;
        var bottom_row_height = document.getElementById("bottom_row").offsetHeight;
        _this.max_height = card_box_height - table_title_height - bottom_row_height - 80 + "px";
      });
    },
    //获取风格图列表
    styleImageList: function styleImageList() {
      var _this2 = this;

      var arg = {
        style_id: this.style_id,
        page: this.page,
        pagesize: 10
      };
      this.loading = true;
      resource.styleImageList(arg).then(function (res) {
        if (res.data.code == 1) {
          _this2.loading = false;
          var data = res.data.data.data;
          data.map(function (item) {
            var image_list = [];

            if (item.img) {
              item.img.split(',').map(function (i) {
                image_list.push(_this2.domain + i);
              });
            }

            item.image_list = image_list;
          });
          _this2.data = data;
          console.log(_this2.data);
          _this2.button_list = res.data.data.button_list;
          _this2.shooting_style_name = res.data.data.shooting_style_name;
          _this2.total = res.data.data.total;
          _this2.i_id = res.data.data.i_id;
        } else {
          _this2.$message.warning(res.data.msg);
        }
      });
    },
    //点击添加或编辑
    addFn: function addFn(type, gallery_id) {
      var _this3 = this;

      this.type = type;
      this.dialog_title = type == '1' ? '添加风格图' : '编辑风格图';

      if (type == '2') {
        this.gallery_id = gallery_id;
        var arg = {
          gallery_id: gallery_id
        };
        resource.editStyleImgGet(arg).then(function (res) {
          if (res.data.code == 1) {
            var data = res.data.data;

            if (data.img) {
              data.img.split(',').map(function (item) {
                var img_obj = {
                  urls: item,
                  show_icon: false
                };

                _this3.img_list.push(img_obj);
              });
            }

            _this3.img = data.img;
            _this3.show_dialog = true;
          } else {
            _this3.$message.warning(res.data.msg);
          }
        });
      } else {
        this.show_dialog = true;
      }
    },
    //监听图片列表回调
    callbackFn: function callbackFn(img_arr) {
      this.img = img_arr;
    },
    //关闭弹窗
    closeDialog: function closeDialog() {
      this.img = []; //已上传的图片列表（参数）

      this.img_list = []; //已上传的图片列表（显示）
    },
    //弹窗提交
    commitFn: function commitFn() {
      var _this4 = this;

      if (this.img.length == 0) {
        this.$message.warning('请上传风格图!');
      } else {
        var arg = {
          img: this.img.join(',')
        };

        if (this.type == '1') {
          //添加
          arg.style_id = this.style_id;
          resource.addStyleImg(arg).then(function (res) {
            if (res.data.code == 1) {
              _this4.$message.success(res.data.msg);

              _this4.show_dialog = false; //获取风格图列表

              _this4.styleImageList();
            } else {
              _this4.$message.warning(res.data.msg);
            }
          });
        } else {
          //编辑
          arg.gallery_id = this.gallery_id;
          resource.editStyleImgPost(arg).then(function (res) {
            if (res.data.code == 1) {
              _this4.$message.success(res.data.msg);

              _this4.show_dialog = false; //获取风格图列表

              _this4.styleImageList();
            } else {
              _this4.$message.warning(res.data.msg);
            }
          });
        } //获取表格最大高度


        this.onResize();
      }
    },
    //点击设为主图
    styleImgChangeMain: function styleImgChangeMain(gallery_id) {
      var _this5 = this;

      this.$confirm('确认设置为主图?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        var arg = {
          gallery_id: gallery_id
        };
        resource.styleImgChangeMain(arg).then(function (res) {
          if (res.data.code == 1) {
            _this5.$message.success(res.data.msg); //获取风格图列表


            _this5.styleImageList();
          } else {
            _this5.$message.warning(res.data.msg);
          }
        });
      }).catch(function () {
        _this5.$message({
          type: 'info',
          message: '已取消'
        });
      });
    },
    //点击删除
    deleteFn: function deleteFn(gallery_id) {
      var _this6 = this;

      this.$confirm('确认删除风格图?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        var arg = {
          gallery_id: gallery_id
        };
        resource.delStyleImg(arg).then(function (res) {
          if (res.data.code == 1) {
            _this6.$message.success(res.data.msg); //获取列表


            _this6.styleImageList();
          } else {
            _this6.$message.warning(res.data.msg);
          }
        });
      }).catch(function () {
        _this6.$message({
          type: 'info',
          message: '已取消'
        });
      });
    },
    //分页
    checkPage: function checkPage(v) {
      this.page = v; //获取分类列表

      this.styleImageList();
    }
  },
  components: {
    TableTitle: TableTitle,
    PaginationWidget: PaginationWidget,
    UploadFile: UploadFile
  }
};
