import TableTitle from "../../components/table_title.vue";
import PaginationWidget from "../../../../components/pagination_widget.vue";
import resource from "../../../../api/chain_resource.js";
import commonResource from "../../../../api/common_resource.js";
export default {
  data: function data() {
    return {
      loading: false,
      data: {},
      //返回数据
      button_list: {},
      //按钮权限
      page: 1,
      //当前页码
      max_height: 0,
      //表格最大高度
      id: "",
      //点击的ID
      show_dialog: false,
      //添加或编辑弹窗
      user_list: [],
      //所有员工列表
      user_id: "",
      //选中的员工
      user_name: "",
      //编辑时的员工姓名
      role_list: [],
      //所有角色列表
      menu_role_ids: [],
      //选中的角色
      dept_list: [],
      //所有部门列表
      dept_names: [],
      //选中的部门
      shop_list: [],
      //店铺列表
      shop_codes: [],
      //选中的店铺列表
      view_type: 1,
      //是否查看记录
      dialog_title: "",
      //弹窗标题
      type: "",
      //弹窗类型
      isIndeterminate: false,
      //当前半全选状态
      checkAll: false //当前全选状态

    };
  },
  created: function created() {
    //获取列表
    this.getData();
  },
  destroyed: function destroyed() {
    window.removeEventListener("resize", function () {});
  },
  mounted: function mounted() {
    //获取表格最大高度
    this.onResize();
    window.addEventListener("resize", this.onResize());
  },
  methods: {
    //监听屏幕大小变化
    onResize: function onResize() {
      var _this = this;

      this.$nextTick(function () {
        var card_box_height = document.getElementById("card_box").offsetHeight;
        var table_title_height = document.getElementById("table_title").offsetHeight;
        var bottom_row_height = document.getElementById("bottom_row").offsetHeight;
        _this.max_height = card_box_height - table_title_height - bottom_row_height - 55 + "px";
      });
    },
    //获取列表
    getData: function getData() {
      var _this2 = this;

      var arg = {
        pagesize: 10,
        page: this.page
      };
      this.loading = true;
      resource.getUserList(arg).then(function (res) {
        if (res.data.code == 1) {
          _this2.loading = false;
          _this2.data = res.data.data;
          _this2.button_list = _this2.data.button_list;
        } else {
          _this2.$message.warning(res.data.msg);
        }
      });
    },
    //点击添加或编辑
    addFn: function addFn(type, user_id) {
      this.type = type;

      if (type == '1') {
        //添加
        this.id = "";
        this.dialog_title = '添加用户'; //创建get

        this.addUserGet();
      } else {
        //编辑
        this.id = user_id;
        this.dialog_title = '编辑用户'; //编辑get

        this.editUserGet();
      }
    },
    //关闭弹窗
    closeDialog: function closeDialog() {
      this.user_id = "";
      this.menu_role_ids = [];
      this.dept_names = [];
      this.shop_codes = [];
      this.view_type = 1;
    },
    //创建get
    addUserGet: function addUserGet() {
      var _this3 = this;

      resource.addUserGet().then(function (res) {
        if (res.data.code == 1) {
          var data = res.data.data;
          _this3.user_list = data.user_list;
          _this3.role_list = data.role_list;
          _this3.dept_list = data.dept_list; //获取店铺列表

          _this3.ajaxViewShop(_this3.dept_names);

          _this3.show_dialog = true;
        } else {
          _this3.$message.warning(res.data.msg);
        }
      });
    },
    //编辑get
    editUserGet: function editUserGet() {
      var _this4 = this;

      var arg = {
        user_id: this.id
      };
      resource.editUserGet(arg).then(function (res) {
        if (res.data.code == 1) {
          var data = res.data.data;
          _this4.user_name = data.info.ding_user_name;
          _this4.role_list = data.menu_role_list;
          _this4.menu_role_ids = data.info.menu_role_ids;
          _this4.dept_list = data.dept_list;
          _this4.dept_names = data.selected_depts; //获取店铺列表

          _this4.ajaxViewShop(_this4.dept_names, data.selected_shops);

          _this4.show_dialog = true;
        } else {
          _this4.$message.warning(res.data.msg);
        }
      });
    },
    //获取店铺列表
    ajaxViewShop: function ajaxViewShop(dept_names, selected_shops) {
      var _this5 = this;

      var arg = {
        type: 2,
        dept_name: dept_names.join(",")
      };
      commonResource.ajaxViewShop(arg).then(function (res) {
        if (res.data.code == 1) {
          _this5.shop_list = res.data.data;

          if (!!selected_shops) {
            _this5.shop_codes = selected_shops;
            _this5.isIndeterminate = selected_shops.length > 0 && selected_shops.length < _this5.shop_list.length;
            _this5.checkAll = selected_shops.length == _this5.shop_list.length;
          } else {
            _this5.shop_codes = [];
            _this5.isIndeterminate = false;
            _this5.checkAll = false;
          }
        } else {
          _this5.$message.warning(res.data.msg);
        }
      });
    },
    //切换选中店铺
    selectedStore: function selectedStore(selected_shops) {
      this.isIndeterminate = selected_shops.length > 0 && selected_shops.length < this.shop_list.length;
      this.checkAll = selected_shops.length == this.shop_list.length;
    },
    //切换是否全选店铺
    checkAllStore: function checkAllStore(val) {
      this.isIndeterminate = false;

      if (val) {
        var arr = [];
        this.shop_list.map(function (item) {
          arr.push(item.shop_code);
        });
        this.shop_codes = arr;
      } else {
        this.shop_codes = [];
      }
    },
    //点击确认
    commitFn: function commitFn() {
      var _this6 = this;

      if (this.type == '1' && this.user_id == '') {
        this.$message.warning('请选择用户!');
        return;
      }

      if (this.menu_role_ids.length == 0) {
        this.$message.warning('请选择角色!');
        return;
      }

      if (this.menu_role_ids.indexOf(1) == -1 && this.dept_names.length == 0) {
        this.$message.warning('请选择部门!');
        return;
      }

      if (this.menu_role_ids.indexOf(1) == -1 && this.shop_codes.length == 0) {
        this.$message.warning('请选择店铺!');
        return;
      }

      var arg = {
        user_id: this.type == '1' ? this.user_id : this.id,
        menu_role_id: this.menu_role_ids.join(','),
        dept_names: this.menu_role_ids.indexOf(1) == -1 ? this.dept_names.join(',') : '-1',
        shop_codes: this.menu_role_ids.indexOf(1) == -1 ? this.shop_codes.join(',') : '-1',
        view_type: this.view_type
      };

      if (this.type == '1') {
        //添加
        resource.addUserPost(arg).then(function (res) {
          if (res.data.code == 1) {
            _this6.$message.success(res.data.msg);

            _this6.show_dialog = false; //获取列表

            _this6.getData();
          } else {
            _this6.$message.warning(res.data.msg);
          }
        });
      } else {
        //编辑
        resource.editUserPost(arg).then(function (res) {
          if (res.data.code == 1) {
            _this6.$message.success(res.data.msg);

            _this6.show_dialog = false; //获取列表

            _this6.getData();
          } else {
            _this6.$message.warning(res.data.msg);
          }
        });
      }
    },
    //分页
    checkPage: function checkPage(v) {
      this.page = v; //获取列表

      this.getData();
    }
  },
  components: {
    TableTitle: TableTitle,
    PaginationWidget: PaginationWidget
  }
};
