import "core-js/modules/es6.function.name";
import resource from "../api/resource.js";
import commonResource from "../api/common_resource.js";
import QuillEditor from "../components/quill_editor.vue";
import UploadFile from "../components/upload_file.vue";
export default {
  data: function data() {
    return {
      show_select: false,
      //选款弹窗
      store_list: [],
      //店铺列表
      shop_code: [],
      //选中的店铺
      need_type: [],
      //需求类型列表
      demand_type: [],
      //选中的需求类型
      delivery_type_list: [],
      //发货类型列表
      send_type: [],
      //选中的发货类型
      demand_date: "",
      //需求日期
      selling_price: "",
      //售卖价格
      remark: "",
      //备注
      banner_list: [],
      //选款弹窗的轮播图
      active_index: 0,
      //当前显示的图片下标
      feekback_dialog: false,
      //反馈弹窗
      feedback_content: "",
      //反馈文字
      feedback_img: [],
      //上传的图片列表
      more_image_dialog: false,
      //更多图片
      image_title_list: ['风格'],
      //更多图片类型
      active_tab_index: 0,
      //选中的下标
      img_arr: [],
      //风格图图片列表
      net_disk_address: "",
      //网盘地址（跳转）
      or_net_disk_address: "",
      //显示地址
      shooting_style_name: "",
      commodity_data: [],
      //封面图数据
      commodity_url: "",
      //封面图网盘地址(显示)
      commodity_open_url: "",
      //封面图网盘地址（可跳转）
      firstTime: '',
      // mousedown的时间戳
      lastTime: '',
      // mouseup的时间戳
      isClick: false,
      // false--禁止点击，true--可点击
      is_loading: false,
      //是否正在提交（防止重复点击）
      toast_dialog: false,
      //有提交过的提示
      toast_content: "",
      //提示内容
      type: 0 //0:提示；1:不提示

    };
  },
  props: {
    //单个商品
    info: {
      type: Object,
      default: {}
    }
  },
  watch: {
    active_tab_index: function active_tab_index(n, o) {
      if (n == 0) {
        //获取风格图
        this.getMoreImage();
      } else {
        //获取封面图
        this.moreImgCommodity();
      }
    },
    //切换24小时内不提示
    type: function type(n, o) {
      this.checkToast(n);
    }
  },
  computed: {
    //图片前缀
    domain: function domain() {
      return this.$store.state.domain;
    },
    //确认选择按钮是否可点击
    disabled: function disabled() {
      return this.shop_code.length == 0 || this.demand_type.length == 0 || this.send_type.length == 0 || this.is_loading;
    }
  },
  methods: {
    mouseDownFn: function mouseDownFn() {
      this.firstTime = new Date().getTime();
    },
    mouseUpFn: function mouseUpFn() {
      this.lastTime = new Date().getTime();

      if (this.lastTime - this.firstTime < 200) {
        this.isClick = true;
      } else {
        this.isClick = false;
      }
    },
    //点击选款
    selectStyle: function selectStyle(style_id) {
      if (this.info.cost_price != '') {
        //获取选款轮播图
        this.chooseBeforGetImg(style_id); //获取店铺列表

        this.ajaxViewShop(); //获取所有需求/发货类型

        this.getAllDemandSendType();
        this.show_select = true;
      } else {
        this.$message.warning('该商品没有成本价,不能选款!');
      }
    },
    //缓存选款的店铺、需求类型、发货类型参数
    changeSelectForm: function changeSelectForm() {
      var form = {
        shop_code: this.shop_code,
        demand_type: this.demand_type,
        send_type: this.send_type
      };
      sessionStorage.setItem("selectedForm", JSON.stringify(form));
    },
    //提交选款
    confirmSelect: function confirmSelect(go_on) {
      var _this = this;

      if (this.shop_code.length == 0) {
        this.$message.warning('请选择店铺!');
      } else if (this.demand_type.length == 0) {
        this.$message.warning('请选择需求类型!');
      } else if (this.send_type.length == 0) {
        this.$message.warning('请选择发货类型!');
      } else if (this.selling_price < 0) {
        this.$message.warning('售卖价格应大于0!');
      } else {
        var shop_code_arr = [];
        var shop_name_arr = [];
        this.shop_code.map(function (item) {
          _this.store_list.map(function (i) {
            if (item == i.shop_code) {
              shop_code_arr.push(i.shop_code);
              shop_name_arr.push(i.shop_name);
            }
          });
        });
        var arg = {
          style_id: this.info.style_id,
          shop_code: shop_code_arr.join(','),
          shop_name: shop_name_arr.join(','),
          demand_type: this.demand_type.join(','),
          send_type: this.send_type.join(','),
          demand_date: this.demand_date ? this.demand_date : "",
          selling_price: this.selling_price,
          remark: this.remark,
          go_on: go_on
        };
        this.is_loading = true;
        resource.chooseGoods(arg).then(function (res) {
          _this.is_loading = false;

          if (res.data.code == 1) {
            _this.$message.success(res.data.msg);

            _this.show_select = false;
            _this.toast_dialog = false;

            _this.changeSelectForm();
          } else if (res.data.code == 5) {
            _this.toast_content = res.data.msg;
            _this.toast_dialog = true;
          } else {
            _this.$message.warning(res.data.msg);
          }
        });
      }
    },
    //切换24小时内不提示
    checkToast: function checkToast(type) {
      var _this2 = this;

      resource.twoFourTitle({
        type: type
      }).then(function (res) {
        if (res.data.code != 1) {
          _this2.$message.warning(res.data.msg);
        }
      });
    },
    //获取选款轮播图
    chooseBeforGetImg: function chooseBeforGetImg(style_id) {
      var _this3 = this;

      var arg = {
        style_id: style_id
      };
      resource.chooseBeforGetImg(arg).then(function (res) {
        if (res.data.code == 1) {
          var img_arr = res.data.data.img;
          var banner_list = [];
          img_arr.map(function (item) {
            banner_list.push(_this3.domain + item);
          });
          _this3.banner_list = banner_list;
        } else {
          _this3.$message.warning(res.data.msg);
        }
      });
    },
    //获取店铺列表
    ajaxViewShop: function ajaxViewShop() {
      var _this4 = this;

      var arg = {
        type: 3
      };
      commonResource.ajaxViewShop(arg).then(function (res) {
        if (res.data.code == 1) {
          _this4.store_list = res.data.data;
          var selectedForm = sessionStorage.getItem("selectedForm");

          if (selectedForm) {
            var new_selected_form = JSON.parse(sessionStorage.getItem("selectedForm"));
            _this4.shop_code = [];
            new_selected_form.shop_code.map(function (item) {
              var arr = _this4.store_list.filter(function (i) {
                return i.shop_code == item;
              });

              if (arr.length > 0) {
                _this4.shop_code.push(arr[0].shop_code);
              }
            });
          }
        } else {
          _this4.$message.warning(res.data.msg);
        }
      });
    },
    //获取所有需求/发货类型
    getAllDemandSendType: function getAllDemandSendType() {
      var _this5 = this;

      commonResource.getAllDemandSendType({
        style_id: this.info.style_id
      }).then(function (res) {
        if (res.data.code == 1) {
          var data = res.data.data; //需求类型

          _this5.need_type = data.filter(function (item) {
            return item.type == 1;
          }); //发货类型

          _this5.delivery_type_list = data.filter(function (item) {
            return item.type == 2;
          });
          _this5.demand_type = [];
          _this5.send_type = [];
          var selectedForm = sessionStorage.getItem("selectedForm");

          if (selectedForm) {
            var new_selected_form = JSON.parse(sessionStorage.getItem("selectedForm"));
            new_selected_form.demand_type.map(function (item) {
              var arr = _this5.need_type.filter(function (i) {
                return i.name == item;
              });

              if (arr.length > 0) {
                _this5.demand_type.push(arr[0].name);
              }
            });

            if (_this5.delivery_type_list.length == 1) {
              _this5.send_type.push(_this5.delivery_type_list[0].name);
            } else {
              new_selected_form.send_type.map(function (item) {
                var arr = _this5.delivery_type_list.filter(function (i) {
                  return i.name == item;
                });

                if (arr.length > 0) {
                  _this5.send_type.push(arr[0].name);
                }
              });
            }
          } else {
            if (_this5.delivery_type_list.length == 1) {
              _this5.send_type.push(_this5.delivery_type_list[0].name);
            }
          }
        } else {
          _this5.$message.warning(res.data.msg);
        }
      });
    },
    //幻灯片自动切换事件
    changeImage: function changeImage(e) {
      this.active_index = e;
    },
    //鼠标移入指示器切换当前显示图片
    checkIndex: function checkIndex(index) {
      this.$refs.cardShow.setActiveItem(index);
    },
    //获取风格图
    getMoreImage: function getMoreImage() {
      var _this6 = this;

      var arg = {
        style_id: this.info.style_id
      };
      resource.moreImgStyle(arg).then(function (res) {
        if (res.data.code == 1) {
          var style_data = res.data.data;
          var img_arr = [];
          style_data.img.map(function (item) {
            img_arr.push(_this6.domain + item);
          });
          _this6.img_arr = img_arr;
          _this6.net_disk_address = style_data.net_disk_address; //网盘地址（跳转）

          _this6.or_net_disk_address = style_data.or_net_disk_address; //显示地址

          _this6.shooting_style_name = style_data.shooting_style_name;
          _this6.more_image_dialog = true;
        } else {
          _this6.$message.warning(res.data.msg);
        }
      });
    },
    //获取封面图
    moreImgCommodity: function moreImgCommodity() {
      var _this7 = this;

      var arg = {
        style_id: this.info.style_id
      };
      resource.moreImgCommodity(arg).then(function (res) {
        if (res.data.code == 1) {
          var commodity_data = res.data.data.img;
          var img_arr = [];
          commodity_data.map(function (item) {
            img_arr.push(_this7.domain + item);
          });
          _this7.commodity_data = img_arr;
          _this7.commodity_url = res.data.data.or_net_disk_address;
          _this7.commodity_open_url = res.data.data.net_disk_address;
          _this7.more_image_dialog = true;
        } else {
          _this7.$message.warning(res.data.msg);
        }
      });
    },
    //点击加入购物车
    addCar: function addCar(cost_price) {
      var _this8 = this;

      if (cost_price == '') {
        this.$message.warning('该商品没有成本价，不能加入待选！');
        return;
      }

      var arg = {
        style_id: this.info.style_id
      };
      resource.addSelectCart(arg).then(function (res) {
        if (res) {
          _this8.$message.success(res.data.msg);

          _this8.$emit('setStatus', _this8.info.style_id);

          var _arg = {
            type: 'add',
            num: 1
          };

          _this8.$store.commit('setCarGoods', _arg);
        }
      });
    },
    //监听富文本编辑器内容变化
    getEditor: function getEditor(v) {
      this.remark = v;
    },
    //关闭选款弹窗
    closeSelectDialog: function closeSelectDialog() {
      this.demand_date = ""; //需求日期

      this.selling_price = ""; //售卖价格

      this.remark = ""; //备注

      this.banner_list = []; //选款弹窗的轮播图

      this.active_index = 0; //当前显示的图片下标
    },
    //上传图片回调
    uploadFn: function uploadFn(v) {
      this.feedback_img = v;
    },
    //提交反馈
    confirmFeekBack: function confirmFeekBack() {
      var _this9 = this;

      if (this.feedback_content == '') {
        this.$message.warning('请输入反馈内容!');
      } else {
        var arg = {
          style_id: this.info.style_id,
          feedback_content: this.feedback_content,
          feedback_img: this.feedback_img.join(',')
        };
        resource.confirmFeekBack(arg).then(function (res) {
          if (res.data.code == 1) {
            _this9.$message.success(res.data.msg);

            _this9.feekback_dialog = false;

            _this9.$emit('callback');
          } else {
            _this9.$message.warning(res.data.msg);
          }
        });
      }
    },
    //关闭反馈弹窗
    closeFeekDialog: function closeFeekDialog() {
      this.feedback_content = ""; //反馈文字

      this.feedback_img = []; //上传的图片列表
    },
    //点击跳转详情
    getDetail: function getDetail() {
      if (!this.isClick) return;
      var active_path = "/goods_detail?style_id=".concat(this.info.style_id);
      var routeData = this.$router.resolve(active_path);
      window.open(routeData.href);
    },
    //点击查看网盘
    windowOpen: function windowOpen(url) {
      if (!this.or_net_disk_address || this.or_net_disk_address.indexOf('https://pan.baidu.com') == -1) {
        this.$message.warning('该地址不是网盘地址格式!');
      } else {
        window.open(url);
      }
    }
  },
  components: {
    QuillEditor: QuillEditor,
    UploadFile: UploadFile
  }
};
