import resource from "../../../../../api/chain_resource.js";
import TableTitle from "../../../components/table_title.vue";
import PaginationWidget from "../../../../../components/pagination_widget.vue";
import UploadFile from "../../../../../components/upload_file.vue";
export default {
  data: function data() {
    return {
      loading: false,
      max_height: 0,
      page: 1,
      //页码
      data: [],
      //获取的数据
      i_id: "",
      button_list: {},
      total: 0,
      commodity_id: "",
      show_dialog: false,
      //弹窗
      type: "",
      dialog_title: "",
      //弹窗标题
      img: [],
      //已上传的图片列表（参数）
      img_list: [] //已上传的图片列表（显示）

    };
  },
  props: {
    //商品ID
    style_id: {
      type: String,
      default: ""
    },
    //商品编号
    style_name: {
      type: String,
      default: ""
    }
  },
  created: function created() {
    //获取列表
    this.upLoadImgList();
  },
  computed: {
    //图片前缀
    domain: function domain() {
      return this.$store.state.domain;
    }
  },
  destroyed: function destroyed() {
    window.removeEventListener("resize", function () {});
  },
  mounted: function mounted() {
    //获取表格最大高度
    this.onResize();
    window.addEventListener("resize", this.onResize());
  },
  methods: {
    //监听屏幕大小变化
    onResize: function onResize() {
      var _this = this;

      this.$nextTick(function () {
        var card_box_height = document.getElementById("card_box").offsetHeight;
        var table_title_height = document.getElementById("table_title").offsetHeight;
        var bottom_row_height = document.getElementById("bottom_row").offsetHeight;
        _this.max_height = card_box_height - table_title_height - bottom_row_height - 55 + "px";
      });
    },
    //获取商品图列表
    upLoadImgList: function upLoadImgList() {
      var _this2 = this;

      var arg = {
        style_id: this.style_id,
        page: this.page,
        pagesize: 10
      };
      this.loading = true;
      resource.upLoadImgList(arg).then(function (res) {
        if (res.data.code == 1) {
          _this2.loading = false;
          var data = res.data.data.data;
          data.map(function (item) {
            var image_list = [];
            image_list.push(_this2.domain + item.img);
            item.image_list = image_list;
          });
          _this2.data = data;
          _this2.button_list = res.data.data.button_list;
          _this2.total = res.data.data.total;
          _this2.i_id = res.data.data.i_id;
        } else {
          _this2.$message.warning(res.data.msg);
        }
      });
    },
    //点击添加或编辑
    addFn: function addFn(type, commodity_id) {
      var _this3 = this;

      this.type = type;
      this.dialog_title = type == '1' ? '添加商品图' : '编辑商品图';

      if (type == '2') {
        this.commodity_id = commodity_id;
        var arg = {
          commodity_id: commodity_id
        };
        resource.editUploadImgGet(arg).then(function (res) {
          if (res.data.code == 1) {
            var data = res.data.data;
            var img_obj = {
              urls: data.img,
              show_icon: false
            };

            _this3.img_list.push(img_obj);

            _this3.img.push(data.img);
          } else {
            _this3.$message.warning(res.data.msg);
          }
        });
      }

      this.show_dialog = true;
    },
    //监听图片列表回调
    callbackFn: function callbackFn(img_arr) {
      this.img = img_arr;
    },
    //关闭弹窗
    closeDialog: function closeDialog() {
      this.img = []; //已上传的图片列表（参数）

      this.img_list = []; //已上传的图片列表（显示）
    },
    //弹窗提交
    commitFn: function commitFn() {
      var _this4 = this;

      if (this.img.length == 0) {
        this.$message.warning('请上传商品图!');
      } else {
        var arg = {
          img: this.img.join(',')
        };

        if (this.type == '1') {
          //添加
          arg.style_id = this.style_id;
          resource.addUploadImg(arg).then(function (res) {
            if (res.data.code == 1) {
              _this4.$message.success(res.data.msg);

              _this4.show_dialog = false; //获取列表

              _this4.upLoadImgList();
            } else {
              _this4.$message.warning(res.data.msg);
            }
          });
        } else {
          //编辑
          arg.commodity_id = this.commodity_id;
          resource.editUploadImgPost(arg).then(function (res) {
            if (res.data.code == 1) {
              _this4.$message.success(res.data.msg);

              _this4.show_dialog = false; //获取列表

              _this4.upLoadImgList();
            } else {
              _this4.$message.warning(res.data.msg);
            }
          });
        }
      }
    },
    //点击删除
    deleteFn: function deleteFn(commodity_id) {
      var _this5 = this;

      this.$confirm('确认删除商品图?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        var arg = {
          commodity_id: commodity_id
        };
        resource.delUploadImg(arg).then(function (res) {
          if (res.data.code == 1) {
            _this5.$message.success(res.data.msg); //获取列表


            _this5.upLoadImgList();
          } else {
            _this5.$message.warning(res.data.msg);
          }
        });
      }).catch(function () {
        _this5.$message({
          type: 'info',
          message: '已取消'
        });
      });
    },
    //设为主图
    changeMainImg: function changeMainImg(commodity_id) {
      var _this6 = this;

      this.$confirm('确认删除商品图?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(function () {
        var arg = {
          commodity_id: commodity_id,
          style_id: _this6.style_id
        };
        resource.changeMainImg(arg).then(function (res) {
          if (res.data.code == 1) {
            _this6.$message.success(res.data.msg); //获取列表


            _this6.upLoadImgList();
          } else {
            _this6.$message.warning(res.data.msg);
          }
        });
      }).catch(function () {
        _this6.$message({
          type: 'info',
          message: '已取消'
        });
      });
    },
    //分页
    checkPage: function checkPage(v) {
      this.page = v; //获取分类列表

      this.upLoadImgList();
    }
  },
  components: {
    TableTitle: TableTitle,
    PaginationWidget: PaginationWidget,
    UploadFile: UploadFile
  }
};
