result:
		.form_card{
			margin-bottom: 16rem;
			.up_down_row{
				display: flex;
				justify-content: flex-end;
				align-items: center;
				.search_title{
					font-size: 14px;
					font-weight: bold;
				}
				.selected_right{
					display: flex;
					align-items: center;
					cursor: pointer;
					.down_arrow{
						margin-left: 5rem;
						transform: rotate(-90deg);
						width: 14rem;
						height: 8rem;
					}
					.rotate{
						transform: rotate(0deg);
					}
				}

			}
			.between{
				justify-content:space-between;
			}
			.form_item{
				margin-bottom:0 !important;
			}
		}
		.card_box{
			flex:1;
			.image{
				height: 100px;
			}
			.item_row{
				display: flex;
				.item_label{
					width: 56px;
					text-align:end;
				}
				.item_value{
					flex: 1;
				}
			}
		}
		.down_box{
			display:flex;
			padding:30rem;
			.upload_box{
				margin-left: 10px;
				position: relative;
				.upload_file{
					position: absolute;
					top: 0;
					bottom: 0;
					left: 0;
					right: 0;
					width: 100%;
					height: 100%;
					opacity: 0;
				}
			}
		}
		.ae_box{
			max-height: 960rem;
			padding:30rem;
			overflow-y: scroll;
		}
		.goods_style_card{
			margin-bottom: 15rem;
		}
		.style_item{
			border: 1px solid #DDDDDD;
			padding: 10rem 20rem;
			font-size: 14rem;
			color: #333333;
			position:relative;
			.delete_style_icon{
				position: absolute;
				top: 0;
				right: 0;
				width: 14rem;
				height: 14rem;
			}
			.add_style_icon{
				margin-right: 6rem;
				width: 14rem;
				height: 14rem;
			}
		}
		.active_item{
			border: 1px solid #FFC998;
			background: #FFFCFA;
			color: #E47A1A;
		}
		.border_bottom{
			border-bottom:1px solid #DDDDDD;
			margin-bottom: 20px;
		}
		.view_box{
			height: 360rem;
			overflow-y: scroll;
	// 		flex:1 1 auto;
      // height: 0;
      // overflow-y: auto;
		}
